import{c as t,b as e,y as o,e as n,o as r}from"./vendor.2128ca7e.js";const a={class:"markdown-body"},c=o('<h2>START</h2><p>Import styles, <strong>or you can customize theme colors</strong> <a href="#/en/doc/theme">Here</a></p><pre><code class="language-js">  import  {createDatePicker} from &#39;better-datepicker&#39;\n  import &#39;better-datepicker/dist/index.css&#39;\n\n</code></pre><p>you can also</p><pre><code class="language-html">  &lt;div id=&quot;wrapper&quot;&gt;\n     &lt;span&gt;Including children, find the first inputElement&lt;/span&gt;\n     &lt;input type=&quot;text&quot;&gt;\n  &lt;/div&gt;\n</code></pre><pre><code class="language-js">  const input = document.querySelector(&#39;#wrapper&#39;)\n  createDatePicker(input)\n</code></pre><h2>OPTIONS</h2><p><em>createDatePicker</em> accepts two parameters. The first is the <strong>InputElement</strong>, and the second is the <strong>configuration item</strong>. For more details <a href="#/en/doc/options">Here</a></p><pre><code class="language-js">  const input = document.querySelector(&#39;#input&#39;)\n  createDatePicker(input,{\n    placement: &#39;bottom&#39;,\n    type: &#39;date&#39;,\n    zIndex: 2000,\n    format: &#39;yyyy/MM/dd&#39;\n  })\n</code></pre><h2>METHODS</h2><pre><code class="language-js">  const input = document.querySelector(&#39;#input&#39;)\n  const picker = createDatePicker(input)\n\n  picker.destroyed() //destroyed the datepicker\n\n  picker.update({placement: &#39;top&#39;}) //Update configuration, destroyed old datepicker\n\n  picker.onChange((date)=&gt;{\n    console.log(&#39;The current date is &#39; + date)\n  })\n\n  picker.getCurrentDate()\n\n  picker.open()  // to open picker\n\n  picker.close() // to close picker\n\n  picker.clear() // to clear picker\n\n  //more\n</code></pre><h2>Locale</h2><p><strong>global config</strong></p><pre><code class="language-js">  import {locale} from &quot;better-datepicker&quot;\n  import zhCn from &#39;better-datepicker/dist/locale_es/zh-cn&#39;    // es\n  //require(&#39;better-datepicker/dist/locale_umd/zh-cn&#39;) // cjs\n  locale(zhCn)\n</code></pre><p>or</p><pre><code class="language-js">  const localeConfig = {\n     name: &quot;en&quot;,\n     weekStart: 0,\n     yearStart: 1,\n     weekdays: [&quot;Su&quot;, &quot;Mo&quot;, &quot;Tu&quot;, &quot;We&quot;, &quot;Th&quot;, &quot;Fr&quot;, &quot;Sa&quot;],\n     months: [&quot;Jan&quot;, &quot;Feb&quot;, &quot;Mar&quot;, &quot;Apr&quot;, &quot;May&quot;, &quot;Jun&quot;, &quot;Jul&quot;, &quot;Aug&quot;, &quot;Sep&quot;, &quot;Oct&quot;, &quot;Nov&quot;, &quot;Dec&quot;],\n     yearFormat: &quot;yyyy&quot;,\n     weekFormat: &#39;yyyy-ww&#39;\n  }\n  locale(en)\n</code></pre><h2>DOCUMENT TODOLIST</h2><table><thead><tr><th>TODO</th><th>VERSION</th></tr></thead><tbody><tr><td>Doc Support Mobile</td><td>0.3</td></tr><tr><td>Support onOpen、onClose</td><td>0.4</td></tr><tr><td>Support readonly、unlinkPanels、className、bordered、panelRender、size</td><td>0.5</td></tr><tr><td>-</td><td>-</td></tr></tbody></table>',18),u={expose:[],setup:o=>(o,u)=>{const p=n("logo"),d=n("install-card-group");return r(),t("div",a,[e("p",null,[e(p)]),e("p",null,[e(d)]),c])}};export default u;
